(79, 'widget_text', 'a:8:{i:1;a:0:{}i:2;a:3:{s:5:\"title\";s:22:\"Información Adicional\";s:4:\"text\";s:609:\"<ul>\r\n	<li>\r\n		<i class=\"fa fa-question-circle-o\"></i><a href=\"<?php echo esc_url(home_url(\'/\'));?>preguntas-frecuentes/\"> Preguntas frecuentes</a>\r\n	</li>\r\n	<li>\r\n		<i class=\"fa fa-file-text\"></i><a href=\"<?php echo esc_url(home_url(\'/\'));?>terminos-y-condiciones/\"> Términos y condiciones</a>\r\n	</li>\r\n	<li>\r\n		<i class=\"fa fa-user-secret\"></i><a href=\"<?php echo esc_url(home_url(\'/\'));?>politica-de-privacidad/\"> Política de privacidad</a>\r\n	</li>\r\n        <li>\r\n		<i class=\"fa fa-wrench\"></i><a href=\"<?php echo esc_url(home_url(\'/\'));?>politica-de-garantia/\"> Política de garantía</a>\r\n	</li>\r\n</ul>\";s:6:\"filter\";b:0;}i:3;a:3:{s:5:\"title\";s:14:\"Formas de Pago\";s:4:\"text\";s:683:\"<?php\r\n$visa = get_template_directory_uri() . \'/../Kyma-child/images/visa.png\';\r\n$masterCard = get_template_directory_uri() . \'/../Kyma-child/images/mastercard.png\';\r\n$diners = get_template_directory_uri() . \'/../Kyma-child/images/diners.png\';\r\n$amex = get_template_directory_uri() . \'/../Kyma-child/images/americanexpress.png\';\r\n$colsubsidio = get_template_directory_uri() . \'/../Kyma-child/images/colsubsidio.png\';\r\n?>\r\n<ul>\r\n	<li><img src=<?php echo $visa?> alt=\"\"></li>\r\n	<li><img src=<?php echo $masterCard?> alt=\"\"></li>\r\n	<li><img src=<?php echo $diners?> alt=\"\"></li>\r\n	<li><img src=<?php echo $amex?> alt=\"\"></li>\r\n	<li><img src=<?php echo $colsubsidio?> alt=\"\"></li>\r\n</ul>\";s:6:\"filter\";b:0;}i:4;a:3:{s:5:\"title\";s:20:\"Servicios Frecuentes\";s:4:\"text\";s:1170:\"<ul>\r\n	<li>\r\n		<a href=\"<?php echo esc_url(home_url(\'/\'));?>servicios/mecanica-rapida/cambio-de-aceite-y-filtros/\">Cambio de aceite y filtros</a>\r\n	</li>\r\n	<li>\r\n		<a href=\"<?php echo esc_url(home_url(\'/\'));?>servicios/mecanica-rapida/cambio-pastillas-de-freno/\">Cambio pastillas de freno</a>\r\n	</li>\r\n	<li>\r\n		<a href=\"<?php echo esc_url(home_url(\'/\'));?>servicios/mecanica-rapida/sincronizacion/\">Sincronización</a>\r\n	</li>\r\n        <li>\r\n		<a href=\"<?php echo esc_url(home_url(\'/\'));?>servicios/mecanica-rapida/cambio-correa-de-reparticion/\">Cambio correa de repartición</a>\r\n	</li>\r\n         <li>\r\n		<a href=\"<?php echo esc_url(home_url(\'/\'));?>servicios/mecanica-rapida/servicio-de-escaner/\">Servicio de escáner</a>\r\n	</li>\r\n        <li>\r\n		<a href=\"<?php echo esc_url(home_url(\'/\'));?>servicios/serviteca/alineacion-y-balanceo/\">Alineación y balanceo</a>\r\n	</li>\r\n        <li>\r\n		<a href=\"<?php echo esc_url(home_url(\'/\'));?>servicios/colision-expres/reparacion-de-abolladuras/\">Reparación de abolladuras</a>\r\n	</li>\r\n        <li>\r\n		<a href=\"<?php echo esc_url(home_url(\'/\'));?>servicios/colision-expres/pintura-general/\">Pintura de piezas</a>\r\n	</li>\r\n</ul>\";s:6:\"filter\";b:0;}i:5;a:3:{s:5:\"title\";s:21:\"Ubicación y Contacto\";s:4:\"text\";s:881:\"<div class=\"widget_overlay\" onclick=\"style.pointerEvents=\'none\'\"></div>\r\n<iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3976.594978096009!2d-74.06535628481947!3d4.66607494325504!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x8e3f9a5870c8ea05%3A0x663c5856d3324ece!2sServitalleres!5e0!3m2!1ses-419!2sco!4v1480098372666\" width=\"400\" height=\"300\" frameborder=\"0\" style=\"border:0\" allowfullscreen></iframe>\r\n<div class=\"contactwidget\">\r\n<p><i class= \"fa fa-map-marker\"></i>  Carrera 22 # 76 - 57, Bogotá - Colombia</p>\r\n<p><i class= \"fa fa-phone\"></i><a href=\"tel:+5712119290\">  2117943 - 2119290</a></p>\r\n<p><i class= \"fa fa-envelope\"></i><a href=\"<?php echo esc_url(home_url(\'/\'));?>contactanos/\">  Formulario de contacto</a></p>\r\n</div>\r\n<div class=\"widget_button\"><a id=\"widget_link\" href=\"<?php echo esc_url(home_url(\'/\'));?>citas/\">Agendar cita</a></div>\";s:6:\"filter\";b:0;}i:7;a:3:{s:5:\"title\";s:24:\"Promociones y Descuentos\";s:4:\"text\";s:177:\"<p>Ofrecemos promociones y descuentos constantemente. Asegúrate de revisar esta sección seguido!<a href=\"<?php echo esc_url(home_url(\'/\'));?>promociones/\"> Ver más »</a></p>\";s:6:\"filter\";b:0;}i:8;a:3:{s:5:\"title\";s:0:\"\";s:4:\"text\";s:285:\"<div class=\"widget_cta\">\r\n<h5>Solicita una cita</h5>\r\n<p>Servicio:</p> \r\n<p>Lunes a Viernes de 8:00 am a 5:15 pm</p>\r\n<p>Sábados de 8:00 am a 2:00 pm</p>\r\n<div class=\"widget_button\"><a id=\"widget_link\" href=\"<?php echo esc_url(home_url(\'/\'));?>citas/\">Agendar cita</a></div>\r\n</div>\r\n\";s:6:\"filter\";b:0;}s:12:\"_multiwidget\";i:1;}', 'yes'),
